export type StackIconNames =
  | 'react'
  | 'nodejs'
  | 'nextjs'
  | 'vercel'
  | 'redux'
  | 'aws'
  | 'tailwind';
export type CompanyIconNames = 'github' | 'linkedin' | 'corellium';
export type UtilityIconsNames = 'location' | 'sun';

export type IconNames = StackIconNames | CompanyIconNames | UtilityIconsNames;

type IconsProps = {
  name: IconNames;
  size?: number;
  className?: string;
};

type IconsMap = {
  [key in IconNames]: FC<IndividualIconProps>;
};

const iconsMap: IconsMap = {
  github: Github,
  linkedin: LinkedIn,
  location: Location,
  sun: Sun,
  corellium: Corellium,
  react: React,
  nodejs: NodeJS,
  nextjs: NextJS,
  vercel: Vercel,
  redux: Redux,
  aws: AWS,
  tailwind: Tailwind,
};

export const Icons: FC<IconsProps> = ({ name, size, className }) => {
  const Icon = iconsMap[name];

  return <Icon size={size} className={className} theme="dark" />;
};
